import React, { useState, useEffect } from 'react';

export const ClientTable = ({ responsible }) => {
  const [clients, setClients] = useState([]);

  useEffect(() => {
    const fetchClients = async () => {
      try {
        const response = await fetch(`http://127.0.0.1:5000/clients?responsible=${encodeURIComponent(responsible)}`);
        const data = await response.json();
        if (response.ok) {
          setClients(data.clients);
        } else {
          console.error('Ошибка при получении списка клиентов');
        }
      } catch (error) {
        console.error('Ошибка при выполнении запроса:', error);
      }
    };

    fetchClients();
  }, [responsible]);

  const updateStatus = async (id, status) => {
    // Добавьте логику для обновления статуса клиента
  };

  return (
    <table>
      <thead>
        <tr>
          <th>Имя</th>
          <th>Статус</th>
          <th>Действие</th>
        </tr>
      </thead>
      <tbody>
        {clients.map((client) => (
          <tr key={client.id}>
            <td>{client.name}</td>
            <td>{client.status}</td>
            <td>
              <select value={client.status} onChange={(e) => updateStatus(client.id, e.target.value)}>
                <option value="В работе">В работе</option>
                <option value="Отказ">Отказ</option>
                <option value="Сделка закрыта">Сделка закрыта</option>
              </select>
            </td>
          </tr>
        ))}
      </tbody>
    </table>
  );
};
